CREATE TABLE tbl_product
(
	id INT UNSIGNED PRIMARY KEY,
    name VARCHAR(120),
    price INT UNSIGNED,
	category varchar(8),
    available bit
);

CREATE TABLE tbl_image
(
    id INT UNSIGNED PRIMARY KEY AUTO_INCREMENT NOT NULL,
    product_id INT UNSIGNED,
    path varchar(100),
    CONSTRAINT FK_ProductImage FOREIGN KEY (product_id) REFERENCES tbl_product(id)
);
   	
CREATE TABLE tbl_user
(
	id INT UNSIGNED PRIMARY KEY AUTO_INCREMENT NOT NULL,
	parent_comment_id INT,
	username varchar(40),
	password varchar(128),
	email varchar(320),
	admin bit
);

CREATE TABLE tbl_comment
(
	id INT UNSIGNED PRIMARY KEY AUTO_INCREMENT NOT NULL,
	product_id INT UNSIGNED,
	user_id INT UNSIGNED,
	content varchar(255),
	timestamp datetime,
    CONSTRAINT FK_ProductComment FOREIGN KEY (product_id) REFERENCES tbl_product(id),
    CONSTRAINT FK_UserComment FOREIGN KEY (user_id) REFERENCES tbl_user(id)
);

CREATE TABLE tbl_description
(
	id INT UNSIGNED PRIMARY KEY AUTO_INCREMENT NOT NULL,
	product_id INT UNSIGNED,
	content varchar(255),
	CONSTRAINT FK_ProductShortDescription FOREIGN KEY (product_id) REFERENCES tbl_product(id)
)






